import React, {Component} from 'react'
import "./Cell.css"


/** A single cell on the board.
 *
 * This has no state --- just two props:
 *
 * - flipCellsAroundMe: a function rec'd from the board which flips this
 *      cell and the cells around of it
 *
 * - isLit: boolean, is this cell lit?
 *
 * This handles clicks --- by calling flipCellsAroundMe
 *
 **/

class Cell extends Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(item) {
    console.log("---------------------")
    console.log("BEFORE: " + item.target.className)
    let flip = this.props.flipCellsAround(this.props.key);
    item.target.className = "Cell" + (flip ? " Cell-lit" : "")
    console.log("AFTER: " + item.target.className)
  }

  render() {
    let classes = "Cell" + (this.props.isLit ? " Cell-lit" : "");
    return (
        <td className={classes} value={this.props.key} onClick={this.handleClick} />
    )
  }
}


export default Cell
